import React, { Component } from 'react';
import Cell from './Cell';
import './Board.css';

class Board extends Component {
  constructor(props){
      super(props);
      this.emptyGrid= this.emptyGrid.bind(this)
  }
      grid = []
      emptyGrid () {
        console.log("hello")
          let grid = [];
          for (let i = 0; i < 9; i++){
              grid[i] = [];
              for (let j = 0; j < 9; j++){
                  grid[i][j] = "";
              }
          }
          console.log(grid)
          return grid
      }

  render() {
    return (
        <div className="board">
            { grid.map((Cell, index)=> (
                <React.Fragment key={index}>
                    { Cell }
                </React.Fragment>
                ))   
            }
            <button className="reset-button">Reset</button>    
        </div>
    );
  }
}

export default Board;
